    @if(!empty($socials))
        <div class="flex justify-center items-center gap-2.5 flex-wrap">
            @foreach($socials as $social)
                @php
                    $url = $social['url'] ?? '#';
                    $platform = $social['platform'] ?? 'link';
                @endphp
                
                <a
                    href="{{ $url }}"
                    target="_blank"
                    rel="noopener noreferrer"
                    class="social-icon-item"
                    title="{{ ucfirst($platform) }}"
                    style="display: flex; align-items: center; justify-content: center; width: 44px; height: 44px; border-radius: 50%; background: #F5F7F6; border: 2px solid #e5e7eb; transition: all 0.3s ease; text-decoration: none;"
                    onmouseover="this.style.background='{{ $hoverColor }}'; this.style.borderColor='{{ $hoverColor }}'; this.style.transform='translateY(-3px)'; this.style.boxShadow='0 4px 12px rgba(34, 184, 166, 0.3)';"
                    onmouseout="this.style.background='#F5F7F6'; this.style.borderColor='#e5e7eb'; this.style.transform='translateY(0)'; this.style.boxShadow='none';"
                >
                    <svg style="width: 20px; height: 20px; fill: #0B0B0B; transition: fill 0.3s ease;" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" onmouseover="this.style.fill='white';" onmouseout="this.style.fill='#0B0B0B';">
                        {!! $getSocialIcon($platform) !!}
                    </svg>
                </a>
            @endforeach
        </div>
    @endif
</div>
